
const PATH = require("path");
const ASSERT = require("assert");

ASSERT.equal(PATH.basename(process.argv[0]), "node");
ASSERT.equal(process.argv[1], __filename);
ASSERT.equal(process.argv[2], "--plugin");

var framework = {
	log: console.log
};

require(process.argv[3]).main(framework, process.argv.slice(4));
